import './App.css';
import React, { Component } from 'react';
import Search from './Search';
import Table from './Table';


const list = [{
    title: 'React',
    url: 'https://facebook.com/',
    author: 'Jordan drake',
    num_comments: 3,
    points: 4,
    objectID: 0
},
{
    title: 'React-DOM',
    url: 'https://gitHub.com/',
    author: 'Michael jordan',
    num_comments: 5,
    points: 7,
    objectID: 1
}
]

// const isSearched = searchValue => item => item.title.toLowerCase.includes(searchValue.toLowerCase());

// function isSearched(searchValue) {
//     return function (item) {
//         return item.title.toLowerCase().includes(searchValue.toLowerCase());
//     }
// }

class App extends Component {

    constructor(props) {

        super(props);

        this.state = {
            list,
            searchValue: '',
        };

        this.onDismiss = this.onDismiss.bind(this);
        this.onSearchChange = this.onSearchChange.bind(this);

    }

    onDismiss(id) {
        const updatedList = this.state.list.filter(item => item.objectID !== id);
        this.setState({ list: updatedList });
    }

    onSearchChange(event) {
        this.setState({ searchValue: event.target.value })
    }

    render() {

        const { searchValue, list } = this.setState;

        return (
            <div className="App" >

                <Search value={searchValue} onChange={this.onSearchChange} > Search </Search>
                <Table list={list} pattern={searchValue} onDismiss={this.onDismiss} />

            </div>
        );
    }

}

export default App;
