import './App.css';
import React, { Component } from 'react';
import Button from './Button';

const DEFAULT_QUERY = 'redux';
const PATH_BASE = 'https://hn.algolia.com/api/v1';
const PATH_SEARCH = '/search';
const PARAM_SEARCH = 'query=';

const url = `${PATH_BASE}${PATH_SEARCH}?${PARAM_SEARCH}${DEFAULT_QUERY}`;

console.log(url);

const largeColumn = {
    width: '40%',
};
const midColumn = {
    width: '30%',
};
const smallColumn = {
    width: '10%',
};

class App extends Component {

    constructor(props) {
        super(props);

        this.state = {
            list: null,
            result: null,
            searchValue: DEFAULT_QUERY,
        };

        this.setSearchTopStories = this.setSearchTopStories.bind(this);
        this.fetchSearchTopStories = this.fetchSearchTopStories.bind(this);
        // this.onSearchChange = this.onSearchChange.bind(this);
        // this.onDismiss = this.onDismiss.bind(this);

    }

    setSearchTopStories(result) {
        alert('Inside setSearchTopStories');
        this.setState({ list: result });
    }

    fetchSearchTopStories(searchValue) {
        alert('Inside fetchSearchTopStories');
        fetch(`${PATH_BASE}${PATH_SEARCH}?${PARAM_SEARCH}${searchValue}`)
            .then(response => response.json)
            .then(result => this.setSearchTopStories(result))
            .catch(e => e);
    }

    // onDismiss(id) {
    //     const updatedList = this.state.list.filter(item => item.objectID !== id);        
    //     this.setState({ list: updatedList });
    // }

    // onSearchChange(event) {
    //     this.setState({ searchValue: event.target.value })
    // }

    componentDidMount() {
        const { searchValue } = this.state;
        this.fetchSearchTopStories(searchValue);
    }

    render() {

        const { result, searchValue } = this.state;

        if (!result) { return null; }

        return (
            <div className="page" >
                {/* <div className="interactions">
                    <Search value={searchValue} onChange={this.onSearchChange} > Search </Search> <br />
                </div> */}
                <Table list={result.hits} pattern={searchValue} onDismiss={this.onDismiss} />
            </div>
        );
    }

}

// const Search = ({ value, onChange, children }) =>
//     <form>
//         {children} <input type="text" value={value} onChange={onChange} />
//     </form>

const Table = ({ result, pattern, onDismiss }) =>
    <div className="table">
        {result.filter(isSearched(pattern)).map(item =>
            <li>
                <div key={item.objectID} className="table-row">
                    <span style={{ largeColumn }}>
                        <a href={item.title}> {item.title}</a>
                    </span>
                    <span style={{ midColumn }}>{item.author}</span>
                    <span style={{ smallColumn }}>{item.num_comments}</span>
                    <span style={{ smallColumn }}>{item.points}</span>
                    <span style={{ smallColumn }}>
                        <Button onClick={() => onDismiss(item.objectID)} className="button-inline">
                            Dismiss
                </Button>
                    </span>
                </div>
            </li>
        )}
    </div>


export default App;

function isSearched(searchValue) {
    return function (item) {
        return item.title.toLowerCase().includes(searchValue.toLowerCase());
    }
}
