import './App.css';
import React, { Component } from 'react';

class App extends Component {
    constructor(props) {

        super(props);

        this.state = {
            list
        };

        this.onDismiss = this.onDismiss.bind(this);

        function onDismiss(id) {

            function noItemId(item) {
                return item.objectID !== id;
            }

            const updatedList = this.state.list.filter(noItemId);
        }
    }

}



const list = [{
    title: 'React',
    url: 'https://facebook.com/',
    author: 'Jordan drake',
    num_comments: 3,
    points: 4,
    objectID: 0
},
{
    title: 'React-DOM',
    url: 'https://gitHub.com/',
    author: 'Michael jordan',
    num_comments: 5,
    points: 7,
    objectID: 1
}
]

// const firstname = 'Himanshu';
