import './App.css';
import React, { Component } from 'react';
import Button from './Button';

const DEFAULT_QUERY = 'redux';
const PATH_BASE = 'https://hn.algolia.com/api/v1';
const PATH_SEARCH = '/search';
const PARAM_SEARCH = 'query=';

const url = `${PATH_BASE}${PATH_SEARCH}?${PARAM_SEARCH}${DEFAULT_QUERY}`;

console.log(url);

const largeColumn = {
    width: '40%',
};
const midColumn = {
    width: '30%',
};
const smallColumn = {
    width: '10%',
};

class App extends Component {

    constructor(props) {
        super(props);

        this.state = {
            result: null,
            searchValue: DEFAULT_QUERY,
        };

        this.setSearchTopStories = this.setSearchTopStories.bind(this);
        this.fetchSearchTopStories = this.fetchSearchTopStories.bind(this);
        this.onSearchChange = this.onSearchChange.bind(this);
        this.onSearchSubmit = this.onSearchSubmit.bind(this);
        this.onDismiss = this.onDismiss.bind(this);

    }

    componentDidMount() {
        const { searchValue } = this.state;
        this.fetchSearchTopStories(searchValue);
    }

    fetchSearchTopStories(searchValue) {
        fetch(`${PATH_BASE}${PATH_SEARCH}?${PARAM_SEARCH}${searchValue}`)
            .then(response => response.json())
            .then(result => this.setSearchTopStories(result))
            .catch(e => e);
    }

    setSearchTopStories(result) {
        this.setState({ result });
    }

    onDismiss(id) {
        console.log('Inside onDismiss: ', JSON.stringify(id));
        const isNotId = item => item.objectID !== id;
        const updatedHits = this.state.result.hits.filter(isNotId);
        this.setState({
            // result: Object.assign({}, this.state.result, { hits: updatedHits })
            result: { ...this.state.result, hits: updatedHits }
        });
    }

    onSearchChange(event) {
        this.setState({ searchValue: event.target.value });
    }

    onSearchSubmit() {
        const { searchValue } = this.state;
        this.fetchSearchTopStories(searchValue);
    }

    render() {

        const { searchValue, result } = this.state;

        if (!result) { return null; }

        return (
            <div className="page" >
                <div className="interactions">
                    <Search value={searchValue} onChange={this.onSearchChange} onSubmit={this.onSearchSubmit}> Search </Search> <br />
                </div>
                { result &&
                    <Table list={result.hits} onDismiss={this.onDismiss} /> // pattern={searchValue}
                }
            </div>
        );
    }

}

export default App;

const Search = ({ value, onChange, onSubmit, children }) =>
    <form onSubmit={onSubmit}>
        <input type="text" value={value} onChange={onChange} />
        <Button type="submit">
            {children}
        </Button>
    </form>

const Table = ({ list, onDismiss }) => // pattern,
    <div className="table">
        {list.map(item =>
            <li>
                <div key={item.objectID} className="table-row">
                    <span style={{ largeColumn }}>
                        <a href={item.title}> {item.title}</a>
                    </span>
                    <span style={{ midColumn }}>{item.author}</span>
                    <span style={{ smallColumn }}>{item.num_comments}</span>
                    <span style={{ smallColumn }}>{item.points}</span>
                    <span style={{ smallColumn }}>
                        <Button onClick={() => onDismiss(item.objectID)} className="button-inline">
                            Dismiss
                        </Button>
                    </span>
                </div>
            </li>
        )}
    </div>

