import './App.css';
import React, { Component } from 'react';


const list = [{
    title: 'React',
    url: 'https://facebook.com/',
    author: 'Jordan drake',
    num_comments: 3,
    points: 4,
    objectID: 0
},
{
    title: 'React-DOM',
    url: 'https://gitHub.com/',
    author: 'Michael jordan',
    num_comments: 5,
    points: 7,
    objectID: 1
}
]

// const isSearched = searchValue => item => item.title.toLowerCase.includes(searchValue.toLowerCase());

function isSearched(searchTerm) {
    return function (item) {
        return item.title.toLowerCase().includes(searchTerm.toLowerCase());
    }
}

class App extends Component {

    constructor(props) {

        super(props);

        this.state = {
            list,
            searchValue: '',
        };

        this.onDismiss = this.onDismiss.bind(this);
        this.onSearchChange = this.onSearchChange.bind(this);

    }

    onDismiss(id) {
        const updatedList = this.state.list.filter(item => item.objectID !== id);
        this.setState({ list: updatedList });
    }

    onSearchChange(event) {
        this.setState({ searchValue: event.target.value })
    }

    render() {
        return (
            <div className="App" >
                <form>
                    <input type="text" onChange={this.onSearchChange}></input>
                </form>

                {
                    this.state.list.filter(isSearched(this.state.searchValue)).map(item => {
                        return (
                            <div key={item.objectID}>
                                <span>
                                    <a href={item.title}> {item.title}</a>
                                </span>
                                <span>{item.author}</span>
                                <span>{item.num_comments}</span>
                                <span>{item.points}</span>
                                <span>
                                    <button
                                        onClick={() => this.onDismiss(item.objectID)}
                                        type="button">
                                        Dismiss
                                </button>
                                </span>
                            </div>
                        );
                    })
                }
            </div>
        );
    }

}

export default App;
